package com.duoduo.duoduocampus.api;import android.text.TextUtils;import com.duoduo.duoduocampus.utils.LogUtil;public class DomainUtil {	private static String BASE_URL;		private static String BASE_URL_TEST = "http://mobapi.meilishuo.com/2.0/";	public static String getAbsoluteUrl(String relativeUrl) {		if (!TextUtils.isEmpty(relativeUrl)				&& (relativeUrl.toLowerCase().startsWith("http") || relativeUrl						.toLowerCase().startsWith("https"))) {			return relativeUrl;		}				if (LogUtil.isDebug()) {			DomainUtil.BASE_URL = BASE_URL_TEST;		}		return DomainUtil.BASE_URL + relativeUrl;	}}